import Post from "../post/Post";
import "./posts.css";

export default function Posts({ posts, isAdmin, user }) {
  console.log(user);
  console.log(posts);
  function renderPosts()
  {
    if(isAdmin)
    {
      posts.map((p) => (
        <Post post={p} />
      ))
    }
    else{
      let userPosts  = posts.map( p => p.username == user.username);
      console.log(userPosts);
    }
  }
  return (
    <div className="posts">
     {renderPosts()}
    </div>
  );
}
